import axios from 'axios';

const baseURL = 'http://localhost:3000'; // Replace with your backend URL

const api = axios.create({
    baseURL,
});

export const getRepositories = () => api.get('/repositories');
export const getRepositoryById = (id) => api.get(`/repositories/${id}`);
