// src/RepositoriesList.js
import React, { useState, useEffect } from 'react';
import { getRepositories } from './api'; // Import your API functions
import { Link } from 'react-router-dom';
import './RepositoriesList.css';

import axios from 'axios';

function RepositoriesList() {
    const [repositories, setRepositories] = useState([]);

    const fetchRepositories = async () => {
        try {
            console.log('Fetching repositories...');
            const response = await axios.get('http://localhost:3000/repositories');
            console.log('Response:', response.data);
            setRepositories(response.data);
        } catch (error) {
            console.error('Error:', error.message);
        }
    };

    useEffect(() => {
        fetchRepositories();
    }, []);

    return (
        <div>
            <h1>Trending GitHub Repositories</h1>
            <button onClick={fetchRepositories}>Refresh</button>
            <ul>
                {repositories.map(repo => (
                    <li key={repo.id}>
                        <strong>{repo.name}</strong> - {repo.stars} stars
                    </li>
                ))}
            </ul>
        </div>
    );
};

