import axios from 'axios';

const API_BASE_URL = 'http://localhost:3000'; // Backend URL

const api = axios.create({
    baseURL: API_BASE_URL,
});

export const getRepositories = () => api.get('/repositories');


// export const getRepositoryById = async (id) => {
//     try {
//         const response = await axios.get(`${API_BASE_URL}/repository/${id}`);
//         return response.data;
//     } catch (error) {
//         throw error;
//     }
// };

export const getRepositoryById = async (id) => {
    try {
        const response = await api.get(`repository/${id}`);
        console.log('api Response:', response.data);
        return response.data;
    } catch (error) {
        console.error('Error:', error.message);
        throw error;
    }
};
