// src/RepositoryDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getRepositoryById } from './api'; // Import your API functions

function RepositoryDetails() {
    const { id } = useParams();
    const [repository, setRepository] = useState(null);

    useEffect(() => {
        // Fetch repository details from API and update state
        getRepositoryById(id)
            .then(response => setRepository(response.data))
            .catch(error => console.error('Error fetching repository details:', error));
    }, [id]);

    if (!repository) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>Repository Details</h2>
            <p>Name: {repository.name}</p>
            <p>Stars: {repository.stars}</p>
            <p>URL: {repository.url}</p>
        </div>
    );
}

export default RepositoryDetails;
