//import { getRepositories } from './api'; // Import your API functions

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './RepositoriesList.css';

function RepositoriesList() {
    const [repositories, setRepositories] = useState([]);

    const fetchRepositories = async () => {
        try {
            console.log('Fetching repositories...');
            const response = await axios.get('http://localhost:3000/repositories');
            console.log('Response:', response.data);
            setRepositories(response.data);
        } catch (error) {
            console.error('Error:', error.message);
        }
    };

    useEffect(() => {
        fetchRepositories();
    }, []);

    return (
        <div className="repositories-list-container">
            <h2 className="section-title">Trending GitHub Repositories</h2>
            <button onClick={fetchRepositories}>Refresh</button>
            <ul className="repositories-list">
                {repositories.map(repo => (
                    <li key={repo.id} className="repository-item">
                        <Link to={`/repository/${repo.id}`} className="repository-link">
                            {/* <strong>{repo.name}</strong> - {repo.stars} stars */}
                            <div className="repository-details">
                                <h3 className="repository-name">{repo.name}</h3>
                                <p className="repository-stars">{repo.stars} stars</p>
                            </div>
                        </Link>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default RepositoriesList;
